import logging
from workerinterfaces import ExecutorInterface, ExecutionResult
from typing import Any, Dict, List
import json
import string


def safe_parse_json(s: str):
 try:
 return json.loads(s)
 except Exception:
 return {}

def safe_lower_no_punct(text: str):
 if not isinstance(text, str):
 return ""
 return text.lower().translate(str.maketrans('', '', string.punctuation)).strip()

def is_system_topic(topic_name: str) -> bool:
 system_topics = {
 "Conversation Start", "Conversation End", "End of Conversation",
 "Conversational boosting", "Escalate", "Fallback", "Multiple Topics Matched",
 "On Error", "Reset Conversation", "Sign in"
 }
 return safe_lower_no_punct(topic_name) in {safe_lower_no_punct(t) for t in system_topics}

def quality_of_agent_instruction(instr: str, mention_tools: bool, length: int) -> (str, List[str]):
 """Evaluate the agent instruction for clarity, structure, and tool mention."""
 # Vague if < 30 chars. Too long if > 1000 chars. Missing tools if not mentioning tools/topics.
 # Should define role, purpose, available tools/topics, limits, style.
 problems = []
 if length < 30:
 problems.append("too_short")
 if length > 1000:
 problems.append("too_long")
 # Look for the phrase 'tool' or 'topic' or any actual tool/topic by name
 if not mention_tools:
 problems.append("missing_tool_topic_reference")
 # If it doesn't mention tone/style/limits
 lower = instr.lower()
 must_words = ["collect", "feedback"] # since this bot's function is feedback collector
 if not any(w in lower for w in must_words):
 problems.append("missing_purpose")
 # Very basic check for limits (e.g., "do not", "limits", etc.)
 if "do not" not in lower and "limit" not in lower:
 problems.append("missing_limits")
 # Basic best practices: could fuzzily look for clarity, completeness
 return ("ok" if len(problems) == 0 else "issue"), problems

def normalize_trigger_queries(trigger_queries):
 # Each trigger may be { "item": ... }, string, or list thereof
 triggers = []
 if isinstance(trigger_queries, list):
 for item in trigger_queries:
 if isinstance(item, dict) and "item" in item:
 triggers.append(str(item["item"]).strip())
 elif isinstance(item, str):
 triggers.append(item.strip())
 elif isinstance(trigger_queries, dict):
 for val in trigger_queries.values():
 triggers.append(str(val).strip())
 elif isinstance(trigger_queries, str):
 triggers.append(trigger_queries.strip())
 return triggers

def meaningful_meta(meta: str) -> bool:
 """Returns True if meta is non-empty, not generic, not whitespace."""
 if not isinstance(meta, str):
 return False
 text = meta.strip().lower()
 # Too generic if empty or commonly used placeholders
 return bool(text) and text not in {"", "topic", "model", "none", "n/a"}

def meta_quality(meta: str) -> str:
 if not isinstance(meta, str) or not meta.strip():
 return "too_short"
 text = meta.strip()
 if len(text) < 10:
 return "too_short"
 # check vague/generic-ness
 too_vague = False
 if text.lower() in {"topic", "model", "none", "n/a"}:
 too_vague = True
 if too_vague:
 return "vague"
 return "good" # Better to flag specifically in pattern objects

class PromptExecutor(ExecutorInterface):
 async def execute(self, logger: logging.Logger, connector_client, input: Dict[str, Any]) -> ExecutionResult:
 logger.info("Starting Copilot Studio bot config evaluation")

 copilot_details_raw = input.get("Copilot_20Details", "")
 bot = safe_parse_json(copilot_details_raw)
 if not isinstance(bot, dict):
 bot = {}

 # Step 1: Normalize/initialize missing fields
 BotId = bot.get("BotId", "")
 BotName = bot.get("BotName", "")
 IsGenerativeOrchestration = bool(bot.get("IsGenerativeOrchestration", False))
 AgentInstructions = bot.get("Agent Instructions") or bot.get("AgentInstructions") or ""
 failed_components = bot.get("failed_components", [])
 Components = bot.get("Components", {})
 if not isinstance(Components, dict):
 Components = {}

 # Normalize Topics
 all_topics = Components.get("Topics", [])
 if not isinstance(all_topics, list):
 all_topics = []
 # Filter out system topics
 user_topics = [t for t in all_topics if not is_system_topic(t.get("TopicName", ""))]

 # Get tools and topic names (present for reference matching)
 tool_names = []
 if isinstance(Components.get("Tools", []), list):
 for t in Components.get("Tools", []):
 if isinstance(t, dict) and "item" in t:
 tool_names.append(str(t["item"]))
 elif isinstance(t, str):
 tool_names.append(str(t))
 topic_names = [t.get("TopicName", "") for t in user_topics]

 # --- 1. Agent Instruction Evaluation ---
 logger.info("Evaluating AgentInstructions for quality and best practices")
 instr_tools_present = any(tool_name.lower() in AgentInstructions.lower() for tool_name in tool_names + topic_names)
 concise_score, agent_instruction_problems = quality_of_agent_instruction(AgentInstructions, instr_tools_present, len(AgentInstructions))
 agent_instruction_quality_issues = 1 if agent_instruction_problems else 0

 pattern_agent_instruction = {
 "pattern": "agent_instruction_issues",
 "title": "Agent Instruction Evaluation",
 "description": "Assesses the bot’s AgentInstructions for clarity, completeness, and adherence to Copilot Studio best practices.",
 "docs": [
 "https://learn.microsoft.com/en-us/microsoft-copilot-studio/authoring-instructions#writing-great-instructions",
 "https://learn.microsoft.com/en-us/microsoft-copilot-studio/guidance/generative-mode-guidance",
 "https://learn.microsoft.com/en-us/microsoft-365-copilot/extensibility/declarative-agent-instructions"
 ],
 "items": [
 {
 "present": bool(AgentInstructions.strip()),
 "concise_score": concise_score,
 "mentions_tools_or_topics": instr_tools_present,
 "missing_best_practices": [{"item": p} for p in agent_instruction_problems],
 "recommendation": "Add concise examples and clarify available tools." if agent_instruction_problems else "Instruction provides good structure and mentions tools/topics.",
 "evidence_examples": [{"item": AgentInstructions[:80] + "..."}] if AgentInstructions else []
 }
 ]
 }

 # --- 2. Model Metadata issues and quality ---
 logger.info("Analyzing topics for model metadata completeness and quality")
 topics_missing_meta = []
 topics_metadata_quality = []
 missing_model_metadata_count = 0
 poor_quality_meta_count = 0
 for topic in user_topics:
 topic_name = topic.get("TopicName", "")
 model_name = topic.get("ModelName", "")
 model_desc = topic.get("ModelDescription", "")
 missing_fields = []
 if not meaningful_meta(model_name):
 missing_fields.append("ModelName")
 if not meaningful_meta(model_desc):
 missing_fields.append("ModelDescription")
 if missing_fields:
 missing_model_metadata_count += 1
 topics_missing_meta.append({
 "topic_name": topic_name,
 "missing_fields": [{"item": f} for f in missing_fields]
 })
 # Check quality even if present
 if model_name.strip() or model_desc.strip():
 qn = meta_quality(model_name)
 qd = meta_quality(model_desc)
 quality_score = "good"
 recommendation = "Both ModelName and ModelDescription are informative."
 if qn != "good" or qd != "good":
 quality_score = f"{qn if qn != 'good' else ''}/{qd if qd != 'good' else ''}".replace("/", "").strip()
 recommendation = "Expand and clarify ModelName and ModelDescription for better topic intent clarity."
 poor_quality_meta_count += 1
 topics_metadata_quality.append({
 "topic_name": topic_name,
 "ModelName": model_name,
 "ModelDescription": model_desc,
 "quality_score": quality_score,
 "recommendation": recommendation
 })

 pattern_model_metadata = {
 "pattern": "model_metadata_issues",
 "title": "Model Metadata Evaluation",
 "description": "Checks whether each topic defines both ModelName and ModelDescription for clarity and discoverability. Missing or empty values reduce orchestration quality.",
 "docs": [
 "https://learn.microsoft.com/en-us/microsoft-copilot-studio/guidance/generative-mode-guidance"
 ],
 "topics": topics_missing_meta
 }
 pattern_model_metadata_quality = {
 "pattern": "model_metadata_quality",
 "title": "Model Metadata Quality",
 "description": "Evaluates whether ModelName and ModelDescription clearly explain the topic’s purpose, scope, or task intent. Generic or vague names/descriptions reduce agent clarity.",
 "docs": [
 "https://learn.microsoft.com/en-us/microsoft-copilot-studio/guidance/generative-mode-guidance",
 "https://learn.microsoft.com/en-us/microsoft-copilot-studio/authoring-instructions#writing-great-instructions"
 ],
 "topics": topics_metadata_quality
 }

 # --- 3. Input Descriptions Evaluation ---
 logger.info("Checking input variables for missing descriptions")
 missing_input_desc_topics = []
 missing_input_descriptions_count = 0
 for topic in user_topics:
 topic_name = topic.get("TopicName", "")
 inputs = topic.get("InputVariables", [])
 missing_inputs = []
 if isinstance(inputs, list):
 for inp in inputs:
 # Some are dicts, some are strings; extract "description", "text", else skip
 if isinstance(inp, dict):
 iname = inp.get("id") or inp.get("text") or inp.get("name")
 desc = inp.get("description", "")
 if iname and (desc is None or not str(desc).strip()):
 missing_inputs.append(str(iname))
 elif isinstance(inp, str):
 # No description possible if string only; count as missing
 missing_inputs.append(inp)
 if missing_inputs:
 missing_input_descriptions_count += 1
 missing_input_desc_topics.append({
 "topic_name": topic_name,
 "missing_inputs": missing_inputs,
 "recommendation": "Add concise descriptions for every input variable."
 })

 pattern_missing_input_desc = {
 "pattern": "missing_input_descriptions",
 "title": "Missing Input Descriptions",
 "description": "Each input variable should have a clear description explaining its purpose and format.",
 "docs": [
 "https://learn.microsoft.com/en-us/microsoft-copilot-studio/guidance/generative-mode-guidance"
 ],
 "topics": [
 {
 "topic_name": t["topic_name"],
 "missing_inputs": t["missing_inputs"],
 "recommendation": t["recommendation"]
 }
 for t in missing_input_desc_topics
 ]
 }

 # --- Summary and scoring ---
 topics_analyzed = len(user_topics)
 failed_count = len(failed_components) if isinstance(failed_components, list) else 0

 issues_summary = {
 "agent_instruction_quality": agent_instruction_quality_issues,
 "missing_model_metadata": missing_model_metadata_count,
 "model_metadata_quality": poor_quality_meta_count,
 "missing_input_descriptions": missing_input_descriptions_count,
 "insufficient_trigger_count": 0,
 "single_trigger_phrase": 0,
 "single_word_trigger": 0,
 "long_trigger_phrases": 0,
 "duplicate_or_similar_triggers": 0,
 "oversized_conditions": 0
 }

 # Scoring (total 100)
 # - Agent Instructions (60%)
 # - Model Metadata (25%)
 # - Input Completeness (15%)
 score_agent_instruction = 40
 score_model_metadata = 30
 score_inputs = 20
 if agent_instruction_quality_issues > 0:
 score_agent_instruction = 10 # harsh penalty if problems
 if missing_model_metadata_count > 0:
 score_model_metadata = max(0, 30 - 10 * missing_model_metadata_count)
 if missing_input_descriptions_count > 0:
 score_inputs = max(0, 20 - 10 * missing_input_descriptions_count)

 conditions_score = 0 # Not used in generative mode

 # Rebalance to correct weights: agent_component should be out of 40, metadata 30, input 20, cond 10 (0 here)
 agent_component = int(score_agent_instruction) # out of 40
 trigger_component = 0
 inputs_component = int(score_inputs) # out of 20
 conditions_component = 0

 # Overall, normalize to 100-point scale
 overall_score = int(agent_component + trigger_component + inputs_component + conditions_component + score_model_metadata * 0.5)

 # --- Patterns ---
 patterns = [
 pattern_agent_instruction,
 pattern_model_metadata,
 pattern_model_metadata_quality,
 pattern_missing_input_desc
 ]

 # Compose result per required schema
 output_json = {
 "BotId": BotId,
 "BotName": BotName,
 "IsGenerativeOrchestration": IsGenerativeOrchestration,
 "overall_score": overall_score,
 "score_breakdown": {
 "agent_component": agent_component,
 "trigger_component": trigger_component,
 "inputs_component": inputs_component,
 "conditions_component": conditions_component
 },
 "Summary": {
 "topics_analyzed": topics_analyzed,
 "failed_components": failed_count,
 "issues_summary": issues_summary
 },
 "Patterns": patterns
 }

 logger.info("Evaluation complete. Returning output.")
 return ExecutionResult(status_code=200, headers={}, body=output_json)
