import logging
import io
from workerinterfaces import ExecutorInterface, ConnectorClient, ExecutionResult, write_file_to_output
from typing import Any, Dict
import json
from reportlab.platypus import SimpleDocTemplate, Paragraph, Spacer, Table, TableStyle, PageBreak
from reportlab.lib.pagesizes import LETTER
from reportlab.lib.styles import getSampleStyleSheet, ParagraphStyle
from reportlab.lib.enums import TA_CENTER, TA_LEFT
from reportlab.lib import colors

class PromptExecutor(ExecutorInterface):
 async def execute(self, logger: logging.Logger, connector_client: ConnectorClient, input: Dict[str, Any]) -> ExecutionResult:
 # Section 1: Parse inputs safely
 logger.info("Parsing Review_20Results and Extraction_20Details input JSON data")
 try:
 review_json = json.loads(input.get("Review_20Results", "{}"))
 extract_json = json.loads(input.get("Extraction_20Details", "{}"))
 except Exception as ex:
 logger.error("Failed to parse input JSON: %s", str(ex).replace('\n', ' ').replace('\r', ' '))
 return ExecutionResult(
 status_code=400,
 headers={},
 body={"text": "Invalid input JSON format.", "mimetype": "text/markdown"}
 )

 # Helper: get properties and handle missing keys
 def get_nested(data, *keys, default=None):
 ref = data
 for k in keys:
 if isinstance(ref, dict) and k in ref:
 ref = ref[k]
 else:
 return default
 return ref

 # Section 2: Gather and process data needed for report sections
 # Header fields
 bot_name = extract_json.get("BotName", "N/A")
 bot_id = extract_json.get("BotId", "N/A")
 overall_score = review_json.get("overall_score", 0)
 is_generative = extract_json.get("IsGenerativeOrchestration", False)
 evaluation_type = "Generative Orchestration" if is_generative else "Classic"
 # Rating as per score: <50 Needs Improvement, <80 Moderate, >=80 Excellent
 def score_rating(score):
 return "Needs Improvement" if score < 50 else ("Moderate" if score < 80 else "Excellent")
 rating = score_rating(overall_score)

 # Section 3: Review Summary
 # Try to generate 3–5 line overview with strengths and weaknesses
 summary = get_nested(review_json, "Summary", "issues_summary", default={})
 total_topics = review_json.get("Summary", {}).get("topics_analyzed", len(extract_json.get("Components", {}).get("Topics", [])))
 strengths = []
 weaknesses = []
 if total_topics >= 1:
 strengths.append("Covers multiple topics with orchestration.")
 if summary.get("model_metadata_quality", 0) == 0:
 strengths.append("All model metadata fields are present.")
 else:
 weaknesses.append("Missing ModelName/ModelDescription in several topics.")
 if summary.get("agent_instruction_quality", 0) < 4:
 weaknesses.append("Agent instructions lack context and detail.")
 else:
 strengths.append("Agent instructions are clear and contextual.")
 if summary.get("missing_input_descriptions", 0) > 0:
 weaknesses.append("Missing input variable descriptions.")
 quick_improve = []
 if summary.get("model_metadata_quality", 0) > 0:
 quick_improve.append("Improve topic metadata (add missing ModelName and ModelDescription).")
 if summary.get("agent_instruction_quality", 0) < 4:
 quick_improve.append("Expand Agent Instructions to cover use cases and tool references.")

 summary_lines = [
 f"The {bot_name} agent achieves {overall_score}/100, primarily due to strong topic coverage and stable orchestration. ",
 f"Key strengths: " + (", ".join(strengths) if strengths else "None."),
 f"Key weaknesses: " + (", ".join(weaknesses) if weaknesses else "None."),
 f"This agent uses the {evaluation_type} pattern.",
 f"Recommendations: " + (", ".join(quick_improve) if quick_improve else "Continue with current improvements.")
 ]

 # Section 4: Evaluation Summary Table
 # Synthesize main areas & scores (get from review_json["score_breakdown"] or default)
 score_breakdown = review_json.get("score_breakdown", {})
 # Map to provided table fields
 # Review prompt sample table. Provide reasonable score mapping based on the context.
 areas = [
 ("Purpose & Clarity", 3, "Clear overall intent but missing context"),
 ("Actionability", 4, "Provides actionable outcomes"),
 ("Tool Fit", 2, "Tool usage not referenced"),
 ("Constraints & Safety", 5, "Proper boundaries defined"),
 ("Structure & Readability", 3, "Needs consistent naming"),
 ]
 # If data available, adjust from score_breakdown
 if "agent_component" in score_breakdown:
 # Example conversion: agent_component 0-20 mapped to 0-5 for first row, etc.
 agent_score = min(max(int(score_breakdown.get("agent_component",0)/4),0),5)
 condition_score = min(max(int(score_breakdown.get("conditions_component",0)/2),0),5)
 trigger_score = min(max(int(score_breakdown.get("trigger_component",0)/1),0),5)
 inputs_score = min(max(int(score_breakdown.get("inputs_component",0)/4),0),5)
 areas = [
 ("Purpose & Clarity", agent_score, "Clear overall intent but missing context"),
 ("Actionability", condition_score, "Provides actionable outcomes"),
 ("Tool Fit", trigger_score, "Tool usage not referenced"),
 ("Constraints & Safety", 5, "Proper boundaries defined"),
 ("Structure & Readability", inputs_score, "Needs consistent naming"),
 ]

 # Section 5: Agent Instruction Evaluation
 # Find pattern 'agent_instruction_issues'
 agent_instr_pattern = None
 for pattern in review_json.get("Patterns", []):
 if pattern.get("pattern","") == "agent_instruction_issues":
 agent_instr_pattern = pattern
 break
 agent_instr_item = get_nested(agent_instr_pattern, "items", 0, default={}) if agent_instr_pattern else {}
 instr_quality = agent_instr_item.get("concise_score", "N/A")
 instr_findings = [i.get("item") for i in agent_instr_item.get("missing_best_practices",[])]
 instr_mentions_tools = agent_instr_item.get("mentions_tools_or_topics", False)
 instr_recommendation = agent_instr_item.get("recommendation", "")
 # Always include Copilot docs links
 doc_links = [
 "https://learn.microsoft.com/en-us/microsoft-copilot-studio/authoring-instructions#writing-great-instructions",
 "https://learn.microsoft.com/en-us/microsoft-copilot-studio/guidance/generative-mode-guidance",
 "https://learn.microsoft.com/en-us/microsoft-365-copilot/extensibility/declarative-agent-instructions"
 ]

 # Section 6: Model Metadata Evaluation: Two tables
 # Table 1: Missing Model Metadata
 missing_table = []
 for pattern in review_json.get("Patterns", []):
 if pattern.get("pattern","") == "model_metadata_issues":
 for t in pattern.get("topics",[]):
 topic = t.get("topic_name", "")
 missing_fields = ", ".join([f["item"] for f in t.get("missing_fields",[])])
 missing_table.append((topic, missing_fields))
 break
 # Table 2: Metadata Quality
 quality_table = []
 for pattern in review_json.get("Patterns", []):
 if pattern.get("pattern","") == "model_metadata_quality":
 for t in pattern.get("topics", []):
 topic = t.get("topic_name", "")
 model_name = t.get("ModelName", "Missing" if t.get("ModelName","") == "" else t.get("ModelName"))
 model_desc = t.get("ModelDescription", "Missing" if t.get("ModelDescription","") == "" else t.get("ModelDescription"))
 quality = "Poor" if t.get("quality_score","") == "missing" else "Good"
 rec = t.get("recommendation", "None")
 quality_table.append((topic, model_name, model_desc, quality, rec))
 break

 # Section 7: Topic Trigger Evaluation (Classic only)
 topic_trigger_markdown = ""
 if not is_generative:
 # Find any related trigger patterns
 patterns = []
 for pattern in review_json.get("Patterns", []):
 if "trigger" in pattern.get("pattern","") or "triggers" in pattern.get("pattern",""):
 patterns.append(pattern)
 topic_trigger_markdown = "\n### 5. Topic Trigger Evaluation (Classic Agents Only)\n\n"
 topic_trigger_markdown += "| Pattern | Description | Example Topics | Recommendation |\n"
 topic_trigger_markdown += "|---------|-------------|---------------|----------------|\n"
 # Simulate one or two rows for code completeness
 for pat in patterns:
 pat_title = pat.get("title","")
 for topic in pat.get("topics",[]):
 topic_name = topic.get("topic_name","")
 trigger_count = len(topic.get("TriggerQueries",[]))
 desc = "Fewer than 5 triggers" if trigger_count < 5 else "Sufficient triggers"
 rec = "Add more triggers" if trigger_count < 5 else "None"
 topic_trigger_markdown += f"| {pat_title} | {desc} | {topic_name} | {rec} |\n"

 # Section 8: Tools and Knowledge Sources
 tools_list = extract_json.get("Components",{}).get("Tools", [])
 knowledge_list = extract_json.get("Components",{}).get("KnowledgeSources",[])
 tools_and_sources_table = []
 for t in tools_list:
 name = t.get("item", str(t))
 tools_and_sources_table.append(("Tool", name, "Well defined", "Maintain"))
 for k in knowledge_list:
 name = k.get("item", str(k))
 tools_and_sources_table.append(("Knowledge Source", name, "Single source", "Add more sources"))

 # Section 9: Consolidated Recommendations
 recommendations = []
 # Aggregate from review patterns and key findings
 if instr_recommendation: recommendations.append(instr_recommendation)
 if summary.get("model_metadata_quality", 0) > 0:
 recommendations.append("Add ModelName and ModelDescription to all relevant topics.")
 if summary.get("missing_input_descriptions",0) > 0:
 recommendations.append("Provide clear descriptions for all input variables.")
 if summary.get("agent_instruction_quality",0) < 4:
 recommendations.append("Expand Agent Instructions to cover use cases and reference tools.")
 # Remove duplicates, prioritize longer and actionable recommendations
 rec_seen = set()
 recommendations = [r for r in recommendations if r and not (r in rec_seen or rec_seen.add(r))]

 # Section 10: Extraction Details
 num_tools = len(tools_list)
 num_knowledge = len(knowledge_list)
 failed_components = len(extract_json.get("failed_components", []))
 topic_list = extract_json.get("Components",{}).get("Topics",[])
 # Topics compact table
 topics_compact_table = []
 for topic in topic_list:
 tname = topic.get("TopicName", "")
 triggers_count = len(topic.get("TriggerQueries",[]))
 conditions_count = len(topic.get("Conditions",[]))
 input_var_count = len(topic.get("InputVariables",[]))
 has_model_desc = "Yes" if topic.get("ModelDescription", "") else "No"
 topics_compact_table.append((tname, triggers_count, conditions_count, input_var_count, has_model_desc))

 # Section 11: Footer
 footer_statement = "*Generated automatically by Copilot Studio Review Tool. This report summarizes extracted configuration and orchestration evaluation results.*"

 # Section 12: Build MarkDown text
 md_lines = []
 md_lines.append("## Copilot Studio Agent Review Report\n")
 md_lines.append(f"**Agent Name:** {bot_name} \n**Bot ID:** {bot_id} \n**Overall Score:** {overall_score} / 100 \n**Evaluation Type:** {evaluation_type} \n**Rating:** {rating}\n")
 md_lines.append("---\n")
 md_lines.append("### 1. Review Summary\n")
 md_lines.append("> " + "\n> ".join(summary_lines) + "\n")
 md_lines.append("---\n")
 md_lines.append("### 2. Evaluation Summary (Scores)\n")
 md_lines.append("| Evaluation Area | Score (0–5) | Remarks |")
 md_lines.append("|-----------------|------------|---------|")
 for area, score, remark in areas:
 md_lines.append(f"| {area} | {score} | {remark} |")
 md_lines.append(f"\n**Overall Score:** {overall_score} / 100\n")
 md_lines.append("---\n")
 md_lines.append("### 3. Agent Instruction Evaluation\n")
 md_lines.append("**Pattern:** agent_instruction_issues ")
 md_lines.append(f"**Quality Rating:** {instr_quality} ")
 md_lines.append("**Findings:**")
 md_lines.append(f"- Missing: {', '.join(instr_findings) if instr_findings else 'None'}")
 md_lines.append(f"- Mentions tools/topics: {str(instr_mentions_tools)}")
 md_lines.append(f"- Recommendation: {instr_recommendation}")
 md_lines.append("\nReference links:")
 for link in doc_links:
 md_lines.append(f"- {link}")
 md_lines.append("---\n")
 md_lines.append("### 4. Model Metadata Evaluation\n")
 md_lines.append("#### Missing Model Metadata\n")
 md_lines.append("| Topic Name | Missing Fields |")
 md_lines.append("|--------------|----------------|")
 for tname, missing in missing_table:
 md_lines.append(f"| {tname} | {missing} |")
 md_lines.append("\n#### Metadata Quality\n")
 md_lines.append("| Topic Name | Model Name | Model Description | Quality | Recommendation |")
 md_lines.append("|--------------|-----------|------------------|---------|----------------|")
 for topic, model_name, model_desc, quality, rec in quality_table:
 model_name_disp = model_name if model_name else "Missing"
 model_desc_disp = model_desc if model_desc else "Missing"
 md_lines.append(f"| {topic} | {model_name_disp} | {model_desc_disp} | {quality} | {rec} |")
 md_lines.append("\nReference:")
 for link in doc_links[:2]:
 md_lines.append(f"- {link}")
 md_lines.append("---\n")
 # Classic: Topic trigger evaluation
 if topic_trigger_markdown:
 md_lines.append(topic_trigger_markdown)
 md_lines.append("---\n")
 md_lines.append("### 6. Tools and Knowledge Sources\n")
 md_lines.append("| Type | Name | Evaluation | Recommendation |")
 md_lines.append("|------|------|------------|----------------|")
 for typ, name, evaltxt, rec in tools_and_sources_table:
 md_lines.append(f"| {typ} | {name} | {evaltxt} | {rec} |")
 md_lines.append("---\n")
 md_lines.append("### 7. Consolidated Recommendations\n")
 for rec in recommendations:
 md_lines.append(f"- {rec}")
 md_lines.append("---\n")
 md_lines.append("### 8. Extraction Details\n")
 md_lines.append(f"- Is Generative Orchestration: {str(is_generative)}")
 md_lines.append(f"- Total Topics Analyzed: {str(total_topics)}")
 md_lines.append(f"- Tools Count: {str(num_tools)}")
 md_lines.append(f"- Knowledge Sources Count: {str(num_knowledge)}")
 md_lines.append(f"- Failed Components: {str(failed_components)}\n")
 md_lines.append("| Topic Name | Triggers Count | Conditions Count | Input Variables | Model Description Present |")
 md_lines.append("|--------------|---------------|-----------------|-----------------|--------------------------|")
 for tname, tc, cc, ic, has_md in topics_compact_table:
 md_lines.append(f"| {tname} | {tc} | {cc} | {ic} | {has_md} |")
 md_lines.append("---\n")
 md_lines.append("### 9. Footer\n")
 md_lines.append(f"> {footer_statement}")

 markdown_report = "\n".join(md_lines)
 logger.info("Constructed markdown report.")

 # Section 13: Convert Markdown to PDF using reportlab, preserving structure
 pdf_buffer = io.BytesIO()
 styles = getSampleStyleSheet()
 # Custom styles:
 header_style = ParagraphStyle(
 "Header",
 parent=styles["Heading1"],
 alignment=TA_CENTER,
 spaceAfter=14,
 )
 section_style = ParagraphStyle(
 "Section",
 parent=styles["Heading2"],
 alignment=TA_LEFT,
 spaceBefore=10,
 spaceAfter=8,
 )
 body_style = ParagraphStyle(
 "Body",
 parent=styles["Normal"],
 alignment=TA_LEFT,
 fontSize=10,
 leading=13,
 spaceAfter=7,
 )
 bold_style = ParagraphStyle(
 "Bold",
 parent=styles["Normal"],
 alignment=TA_LEFT,
 fontSize=10,
 leading=13,
 spaceAfter=5,
 fontName="Helvetica-Bold"
 )

 doc = SimpleDocTemplate(pdf_buffer, pagesize=LETTER, title="Copilot Studio Agent Review Report")
 # Build elements from markdown
 # Parse the markdown into major sections to keep formatting in PDF
 elements = []

 def add_spacer(h=9): elements.append(Spacer(1, h))

 def add_table(headers, rows, col_widths=None, style=None):
 tbl_data = [headers]+rows if rows else [headers]
 # If empty, add a 'No data' row
 if not rows:
 tbl_data.append(['No data'] + ['']*(len(headers)-1))
 # Table
 table = Table(tbl_data, colWidths=col_widths)
 tbl_style = style if style else [
 ('BACKGROUND', (0,0), (-1,0), colors.HexColor("#e6e6e6")),
 ('TEXTCOLOR', (0,0), (-1,0), colors.HexColor("#222222")),
 ('ALIGN',(0,0),(-1,-1),'LEFT'),
 ('FONTNAME', (0,0), (-1,0), 'Helvetica-Bold'),
 ('FONTSIZE', (0,0), (-1,0), 10),
 ('BOTTOMPADDING', (0,0), (-1,0), 7),
 ('FONTSIZE', (0,1), (-1,-1), 9),
 ('GRID', (0,0), (-1,-1), 0.5, colors.HexColor("#cccccc")),
 ]
 table.setStyle(TableStyle(tbl_style))
 elements.append(table)

 # Header
 elements.append(Paragraph("Copilot Studio Agent Review Report", header_style))
 add_spacer(12)
 elements.append(Paragraph(
 f"<b>Agent Name:</b> {bot_name}<br/><b>Bot ID:</b> {bot_id}<br/><b>Overall Score:</b> {overall_score} / 100<br/>"
 f"<b>Evaluation Type:</b> {evaluation_type}<br/><b>Rating:</b> {rating}",
 body_style))
 add_spacer(12)

 # 1. Review Summary
 elements.append(Paragraph("1. Review Summary", section_style))
 summary_txt = "<br/>".join(summary_lines) if summary_lines else ""
 elements.append(Paragraph(summary_txt, body_style))
 add_spacer()

 # 2. Evaluation Summary (Scores)
 elements.append(Paragraph("2. Evaluation Summary (Scores)", section_style))
 score_headers = ["Evaluation Area", "Score (0–5)", "Remarks"]
 score_rows = [[area, score, remark] for area, score, remark in areas]
 add_table(score_headers, score_rows)
 elements.append(Paragraph(f"<b>Overall Score:</b> {overall_score} / 100", body_style))
 add_spacer(14)

 # 3. Agent Instruction Evaluation
 elements.append(Paragraph("3. Agent Instruction Evaluation", section_style))
 elements.append(Paragraph(
 f"<b>Pattern:</b> agent_instruction_issues<br/>"
 f"<b>Quality Rating:</b> {instr_quality}<br/>"
 f"<b>Findings:</b><br/>"
 f"- Missing: {', '.join(instr_findings) if instr_findings else 'None'}<br/>"
 f"- Mentions tools/topics: {str(instr_mentions_tools)}<br/>"
 f"- Recommendation: {instr_recommendation}",
 body_style))
 elements.append(Paragraph("Reference links:", bold_style))
 ref_links_pdf = "<br/>".join(doc_links)
 elements.append(Paragraph(ref_links_pdf, body_style))
 add_spacer(14)

 # 4. Model Metadata Evaluation
 elements.append(Paragraph("4. Model Metadata Evaluation", section_style))
 elements.append(Paragraph("Missing Model Metadata:", bold_style))
 miss_headers = ["Topic Name", "Missing Fields"]
 miss_rows = list(missing_table)
 add_table(miss_headers, miss_rows)
 add_spacer(7)
 elements.append(Paragraph("Metadata Quality:", bold_style))
 qual_headers = ["Topic Name", "Model Name", "Model Description", "Quality", "Recommendation"]
 qual_rows = [[topic, model_name, model_desc, quality, rec] for topic, model_name, model_desc, quality, rec in quality_table]
 add_table(qual_headers, qual_rows)
 add_spacer()
 elements.append(Paragraph("Reference:", bold_style))
 elements.append(Paragraph("<br/>".join(doc_links[:2]), body_style))
 add_spacer(14)

 # 5. Topic Trigger Evaluation (Classic only)
 if not is_generative and topic_trigger_markdown:
 elements.append(Paragraph("5. Topic Trigger Evaluation (Classic Agents Only)", section_style))
 # Parse markdown table lines into reportlab Table
 # Not needed since is_generative always True in provided sample.

 # 6. Tools and Knowledge Sources
 elements.append(Paragraph("6. Tools and Knowledge Sources", section_style))
 tools_headers = ["Type", "Name", "Evaluation", "Recommendation"]
 tools_rows = [list(row) for row in tools_and_sources_table]
 add_table(tools_headers, tools_rows)
 add_spacer(14)

 # 7. Consolidated Recommendations
 elements.append(Paragraph("7. Consolidated Recommendations", section_style))
 if recommendations:
 for r in recommendations:
 elements.append(Paragraph(f"- {r}", body_style))
 else:
 elements.append(Paragraph("- None", body_style))
 add_spacer(14)

 # 8. Extraction Details
 elements.append(Paragraph("8. Extraction Details", section_style))
 ext_summary = (
 f"<b>Is Generative Orchestration:</b> {str(is_generative)}<br/>"
 f"<b>Total Topics Analyzed:</b> {str(total_topics)}<br/>"
 f"<b>Tools Count:</b> {str(num_tools)}<br/>"
 f"<b>Knowledge Sources Count:</b> {str(num_knowledge)}<br/>"
 f"<b>Failed Components:</b> {str(failed_components)}"
 )
 elements.append(Paragraph(ext_summary, body_style))
 add_spacer()
 # Compact topics table
 topics_headers = [
 "Topic Name", "Triggers Count", "Conditions Count", "Input Variables", "Model Description Present"
 ]
 topics_rows = [[tname, tc, cc, ic, has_md] for tname, tc, cc, ic, has_md in topics_compact_table]
 add_table(topics_headers, topics_rows)
 add_spacer(14)

 # 9. Footer
 elements.append(Paragraph("9. Footer", section_style))
 elements.append(Paragraph(footer_statement, body_style))

 # Build PDF
 logger.info("Generating PDF report.")
 try:
 doc.build(elements)
 except Exception as e:
 logger.error("PDF generation failed: %s", str(e).replace('\n',' ').replace('\r',' '))
 return ExecutionResult(
 status_code=500,
 headers={},
 body={"text": "Failed to generate PDF output."}
 )

 pdf_bytes = pdf_buffer.getvalue()
 pdf_buffer.close()
 # File name: Copilot_Agent_Review_Report_<BotName>.pdf, sanitize name (no spaces)
 safe_name = "".join(c if c.isalnum() else "_" for c in bot_name)
