parameters.item/cat_resultcode:
@if(
    empty(body('Filter_array:_Messages_from_Bot')), 
    4,
    if(
        equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 3),
        if(
            equals(
                json(string(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['attachments'])),
                json(string(items('Apply_to_each_Copilot_Test')?['cat_expectedattachmentsjson']))
            ),
            1,
            2
        ),
        if(
            not(empty(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'])),
            if(
                or(
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'ContentError'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'DataLossPreventionViolation'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'FlowActionException'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'FlowActionBadRequest'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'FlowActionTimedOut'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'InvalidContent'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'InfiniteLoopInBotContent'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'LatestPublishedVersionNotFound'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'RedirectToDisabledDialog'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'RedirectToNonExistentDialog'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'SystemError')
                ),
                4,
                if(
                    equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 1),
                    if(
                        equals(
                            items('Apply_to_each_Copilot_Test')?['cat_expectedresponse'],
                            body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text']
                        ),
                        1,
                        2
                    ),
                    if(
                        equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 2),
                        if(
                            equals(body('Get_Copilot_Configuration')?['cat_isenrichedwithconversationtranscripts'], true),
                            5,
                            3
                        ),
                        if(
                            equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 4),
                            if(
                                or(
                                    equals(items('Apply_to_each_Copilot_Test')?['cat_generativeansweroutcomecode'], 1),
                                    equals(items('Apply_to_each_Copilot_Test')?['cat_generativeansweroutcomecode'], 2)
                                ),
                                if(
                                    equals(body('Get_Copilot_Configuration')?['cat_isgeneratedanswersanalysisenabled'], true),
                                    5,
                                    3
                                ),
                                if(
                                    or(
                                        equals(items('Apply_to_each_Copilot_Test')?['cat_generativeansweroutcomecode'], 3),
                                        equals(items('Apply_to_each_Copilot_Test')?['cat_generativeansweroutcomecode'], 4)
                                    ),
                                    if(
                                        equals(body('Get_Copilot_Configuration')?['cat_isazureapplicationinsightsenabled'], true),
                                        5,
                                        3
                                    ),
                                    3
                                )
                            ),
                            5
                        )
                    )
                )
            ),
            3
        )
    )
)

parameters.item/cat_latencyms:
@if(
    or(
        empty(body('Filter_array:_Messages_from_Bot')),
        empty(body('Filter_array:_Messages_from_User'))
    ),
    null,
    div(
        sub(
            ticks(
                body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['timestamp']
            ),
            ticks(
                body('Filter_array:_Messages_from_User')[0]?['timestamp']
            )
        ),
        10000
    )
)

parameters.item/cat_response:
@if(
    empty(body('Filter_array:_Messages_from_Bot')),
    'No response',
    if(
        empty(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text']),
        if(
            not(empty(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['attachments'])),
            'No response, but attachments (Adaptive Cards, etc.)',
            'No response'
        ),
        body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text']
    )
)

parameters.item/cat_resultreason:
@if(
    empty(body('Filter_array:_Messages_from_Bot')),
    'No response from bot',
    if(
        equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 3),
        if(
            equals(
                json(string(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['attachments'])),
                json(string(items('Apply_to_each_Copilot_Test')?['cat_expectedattachmentsjson']))
            ),
            'Exact match between the expected attachment(s) JSON and the received attachment(s) JSON',
            'Not an exact match between the expected attachment(s) JSON and the received attachment(s) JSON'
         ),
        if(
            not(empty(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'])),
            if(
                or(
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'ContentError'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'DataLossPreventionViolation'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'FlowActionException'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'FlowActionBadRequest'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'FlowActionTimedOut'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'InvalidContent'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'InfiniteLoopInBotContent'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'LatestPublishedVersionNotFound'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'RedirectToDisabledDialog'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'RedirectToNonExistentDialog'),
                    contains(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text'], 'SystemError')
                ),
                'The bot returned an error',
                if(
                    equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 1),
                    if(
                        equals(
                            items('Apply_to_each_Copilot_Test')?['cat_expectedresponse'],
                            body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['text']
                        ),
                        'Exact match between the expected message and the received message',
                        'Not an exact match between the expected response and received message'
                     ),
                    if(
                        equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 2),
                        if(
                            equals(body('Get_Copilot_Configuration')?['cat_isenrichedwithconversationtranscripts'], true),
                            'Pending analysis of Conversation Transcripts',
                            'Cannot be evaluated without Conversation Transcripts enrichment'
                        ),
                        if(
                            equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 4),
                            if(
                                or(
                                    equals(items('Apply_to_each_Copilot_Test')?['cat_generativeansweroutcomecode'], 1),
                                    equals(items('Apply_to_each_Copilot_Test')?['cat_generativeansweroutcomecode'], 2)
                                ),
                                if(
                                    equals(body('Get_Copilot_Configuration')?['cat_isgeneratedanswersanalysisenabled'], true),
                                    'Pending analysis with AI Builder',
                                    'Cannot be evaluated without AI Builder analysis'
                                ),
                                if(
                                    or(
                                        equals(items('Apply_to_each_Copilot_Test')?['cat_generativeansweroutcomecode'], 3),
                                        equals(items('Apply_to_each_Copilot_Test')?['cat_generativeansweroutcomecode'], 4)
                                    ),
                                    if(
                                        equals(body('Get_Copilot_Configuration')?['cat_isazureapplicationinsightsenabled'], true),
                                        'Pending analysis with Azure Application Insights',
                                        'Cannot be evaluated without Azure Application Insights enrichment'
                                    ),
                                    'Answer could not be evaluated'
                                )
                            ),
                            'Pending analysis'
                        )
                    )
                )
            ),
            'Answer could not be evaluated'
        )
    )
)

Get_Id.inputs:
@if(
    empty(
        first(
            body('Filter_Attachments')
        )?['attachments']?[0]?['content']?['tokenExchangeResource']?['id']
    ),
    'Not Found',
    first(
        body('Filter_Attachments')
    )?['attachments']?[0]?['content']?['tokenExchangeResource']?['id']
)

inputs.where:
@and(
    equals(item()?['from']?['role'], 'bot'),
    empty(first(item()?['attachments'])?['content']?['tokenExchangeResource']?['id']),
    or(
        equals(first(item()?['attachments'])?['contentType'], 'application/vnd.microsoft.card.adaptive'),
        and(
            not(empty(item()?['text'])),
            not(
                or(
                    contains(toLower(item()?['text']), 'need you to sign in'),
                    contains(toLower(item()?['text']), 'Please sign in'),
                    contains(toLower(item()?['text']), 'sign in to continue'),
                    contains(toLower(item()?['text']), 'log in to access'),
                    contains(toLower(item()?['text']), 'sign-in required'),
                    contains(toLower(item()?['text']), 'click here to log in')
                )
            )
        )
    )
)
