                    if(
                        not(empty(first(body('Filter_array_with_valueType_IntentCandidates'))?['valueType'])),
                        first(body('Filter_array_with_valueType_IntentCandidates'))?['valueType'],
                        if(
                            not(empty(outputs('Get_Topic_Name')?['body']?['name'])),
