Compose:_Attachments_Result.inputs:
@if(
    equals(items('Apply_to_each_Copilot_Test')?['cat_testtypecode'], 3),
    if( 
        equals(items('Apply_to_each_Copilot_Test')?['cat_comparisonoperator'], 10),
        if(
            equals(outputs('Compose:_Adaptive_Card_AI_Validation_Output'),'1'),
            true,
            false
        ),
        or(
            and(
                or(
                    equals(items('Apply_to_each_Copilot_Test')?['cat_comparisonoperator'], 1),
                    equals(items('Apply_to_each_Copilot_Test')?['cat_comparisonoperator'], null)
                ),
                equals(
                    json(string(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['attachments'])),
                    json(string(items('Apply_to_each_Copilot_Test')?['cat_expectedattachmentsjson']))
                )
            ),
            and(
                equals(items('Apply_to_each_Copilot_Test')?['cat_comparisonoperator'], 2),
                not(equals(
                    json(string(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['attachments'])),
                    json(string(items('Apply_to_each_Copilot_Test')?['cat_expectedattachmentsjson']))
                ))
            ),
            and(
                equals(items('Apply_to_each_Copilot_Test')?['cat_comparisonoperator'], 3),
                contains(
                    string(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['attachments']),
                    string(items('Apply_to_each_Copilot_Test')?['cat_validationinstructions'])
                )
            ),
            and(
                equals(items('Apply_to_each_Copilot_Test')?['cat_comparisonoperator'], 4),
                not(contains(
                    string(body('Filter_array:_Messages_from_Bot')[outputs('Message_Position')]?['attachments']),
                    string(items('Apply_to_each_Copilot_Test')?['cat_validationinstructions'])
                ))
            )
        )
    ),
    false
)

