inputs.where:
@or(
    equals(item()?['schemaname'], items('Apply_to_each:_Steps')),
    and(
        startsWith(items('Apply_to_each:_Steps'), 'MCP:'),
        equals(
            split(items('Apply_to_each:_Steps'), ':')[1],
            item()?['schemaname']
        )
    )
)

Compose:_Tool_Name.inputs:
@if(
    startsWith(items('Apply_to_each:_Steps'), 'MCP:'),
    last(split(items('Apply_to_each:_Steps'), ':')),
    if(
        and(
            not(equals(outputs('Compose:_Tool_Record')?['data'], null)),
            contains(outputs('Compose:_Tool_Record')?['data'], 'modelDisplayName: ')
        ),
        trim(
            replace(
                replace(
                    coalesce(
                        if(
                            contains(outputs('Compose:_Tool_Record')?['data'], 'modelDescription:'),
                            split(split(outputs('Compose:_Tool_Record')?['data'], 'modelDisplayName:')[1], 'modelDescription:')[0],
                            null
                        ),
                        if(
                            contains(outputs('Compose:_Tool_Record')?['data'], 'outputs:'),
                            split(split(outputs('Compose:_Tool_Record')?['data'], 'modelDisplayName:')[1], 'outputs:')[0],
                            null
                        ),
                        if(
                            contains(outputs('Compose:_Tool_Record')?['data'], 'action:'),
                            split(split(outputs('Compose:_Tool_Record')?['data'], 'modelDisplayName:')[1], 'action:')[0],
                            null
                        ),
                        split(split(outputs('Compose:_Tool_Record')?['data'], 'modelDisplayName:')[1], '\r\n')[0]
                    ),
                    '\r\n', ''
                ),
                '\n', ''
            )
        ),
        null
    )
)

Compose:_Flow_Id.inputs:
@if(
  and(
    not(equals(outputs('Compose:_Tool_Record')?['data'], null)),
    contains(outputs('Compose:_Tool_Record')?['data'], 'flowId: ')
  ),
  trim(
    replace(
      replace(
        split(
          split(outputs('Compose:_Tool_Record')?['data'], 'flowId:')[1],
          'outputMode:'
        )[0],
        '\r\n',
        ''
      ),
      '\n',
      ''
    )
  ),
  null
)

inputs.value:
@if( not(equals(outputs('Compose:_Tool_Name'), null)),
	outputs('Compose:_Tool_Name'),
	if( not(equals(outputs('Compose:_Flow_Id'), null)),
		outputs('Get_a_row_by_ID_from_selected_environment:_Get_Flow_Name')?['body/name'],
		outputs('Compose:_Tool_Record')['name']
	)	
)

parameters.item/cat_resultreason:
@if( greaterOrEquals(outputs('Compose:_Actual_Tools_Ratio'), items('Apply_to_each:_Test_Result')?['cat_copilottestid/cat_passthreshold']),
	concat('Pass threshold was met(', outputs('Compose:_Actual_Tools_Ratio'), '/', items('Apply_to_each:_Test_Result')?['cat_copilottestid/cat_passthreshold'], ').'),
	concat('Pass threshold was not met(', outputs('Compose:_Actual_Tools_Ratio'), '/', items('Apply_to_each:_Test_Result')?['cat_copilottestid/cat_passthreshold'], ').')
)

